    def __init__(self, screensize):
        ''' constructor '''
        

    def numberCleanup(self, image):
        gray = cv2.cvtColor(image, cv2.COLOR_RGB2GRAY)
        binary_output = np.zeros_like(image)
        binary_output[image >= 56] = 1
        return binary_output

    def getDigitFromImageBinary(self, score_bin):
        digit = -1
        flat = score_bin.flatten()
        print(flat)
        results = np.apply_along_axis(lambda row: sum(row == flat), 1, self.NUMBERS)
        tmp = np.copy(results)
        results[results < 190] = 0
        if np.count_nonzero(results) > 0:
            digit = np.argmax(results)

        # if digit == -1:
            # self.file.write("Identified number: {}\n{}\n{}\n{}\n\n".format(digit, tmp, flat, score_bin))

        return digit


    def getScore(self, image):
        score = -1
        tl = np.copy(self.SCOREBOX)
        br = tl + self.SCORE_SIZE

        score_img = image[tl[1]:br[1], tl[0]:br[0]]
        score_bin = self.numberCleanup(score_img)
        iters = 0
        while (np.count_nonzero(score_bin)):
            if score == -1:
                score = 0

            digit = self.getDigitFromImageBinary(score_bin)
            if digit == -1:
                # self.file.write("Identified number: {}\n{}\n{}\n\n".format(score, score_bin, score_bin.flatten()))
                return -1

            score += digit * (10 ** iters)

            tl[0] = tl[0] - self.SCORE_SIZE[0] - self.SCORE_BUFFER
            br[0] = tl[0] + self.SCORE_SIZE[0]
            score_img = image[tl[1]:br[1], tl[0]:br[0]]
            score_bin = self.numberCleanup(score_img)
            iters += 1;

        return score
